'                                                                                                                               '];

    % If no index provided, return all maps
    if nargin == 0
        string_map = maps;
        return;
    end
    
    switch map_index
        case 1
            string_map = maps.StrMap1;
        case 2
            string_map = maps.StrMap2;
        case 3
            string_map = maps.StrMap3;
        case 4
            string_map = maps.StrMap4;
        case 5
            string_map = maps.StrMap5;
        case 6
            string_map = maps.StrMap6;
        case 7
            string_map = maps.StrMap7;
        case 8
            string_map = maps.StrMap8;
        case 9
            string_map = maps.StrMap9;
        otherwise
            error('Invalid map index: %d. Valid indices are 1-7.', map_index);
    end

end